mapper.select:
select *
from (
             select t1.*,
                    if(find_in_set(pfid, @pids) > 0, @pids := concat(@pids, ',', fid), 0) as ischild
             from (
                          select fid, pfid, name
                          from eoffice_folder t
                          where deleted = 0
                          order by pfid, fid
                          ) t1,
                  (select @pids := #{fid,jdbcType=BIGINT}) t2
             ) t3
where ischild != 0;
